create_itinerary:
curl -X POST http://localhost:6000/api/itinerary/add -H "Content-Type: application/json" -H "access-token: YOUR_ACCESS_TOKEN" -d '{
  "title": "Summer Vacation",
  "description": "Trip to the beach",
  "startDate": "2024-07-01",
  "endDate": "2024-07-15",
  "destinations": [
    {
      "location": "Miami",
      "arrivalDate": "2024-07-01",
      "departureDate": "2024-07-05",
      "notes": "Beach resort"
    }
  ]
}'

edit_itinerary:
curl -X PUT http://localhost:6000/api/itinerary/edit/YOUR_ITINERARY_ID -H "Content-Type: application/json" -H "access-token: YOUR_ACCESS_TOKEN" -d '{
  "title": "Summer Vacation - Updated",
  "description": "Trip to the beach and mountains",
  "startDate": "2024-07-01",
  "endDate": "2024-07-20",
  "destinations": [
    {
      "location": "Miami",
      "arrivalDate": "2024-07-01",
      "departureDate": "2024-07-05",
      "notes": "Beach resort"
    },
    {
      "location": "Aspen",
      "arrivalDate": "2024-07-06",
      "departureDate": "2024-07-15",
      "notes": "Mountain resort"
    }
  ]
}'
